import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person.js'
import person from './Person/Person.js';

class App extends Component {
  state = {
    persons : [
      {id: 1, name: 'Shubham', age : 28},
      {id: 2, name : 'Sakib', age : 29}
    ],
    showPersons : false
  }

  deletePersonHandler = (index) => {
    //const persons = this.state.persons.slice();
    const persons = [...this.state.persons];
    persons.splice(persons, 1);
    this.setState({persons:persons});
  }

  togglePersonsHandler = () =>  {
    const isVisible = this.state.showPersons;
    this.setState({showPersons:!isVisible});
  }

  nameChangedHandler = (event, id) => {
    this.setState({persons: [
      {name: event.target.value , age : 28},
      {name : 'Sakib', age : 29}
    ]});
  }

  render() {
    const style={
      backgroundColor:'white',
      padding:'8px',
      border:'1px solid blue',
      curson:'pointer'
    }

    let persons = null;

    if(this.state.showPersons){
      persons = (
        <div>
          {this.state.persons.map((person, index) => {
            return <Person 
            key={person.id}
            name={person.name} 
            age={person.age}
            click={this.deletePersonHandler.bind(this, index)}
            changed={this.nameChangedHandler.bind(this, event, id)} />
          })}          
        </div>
      );
    }

    return (
      <div>
        <h1>Meet the Person component</h1>
        <button
        style={style}
         onClick={this.togglePersonsHandler}>Switch name</button>  
         {persons}      
      </div>
    );
  }
}

export default App;
