import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person.js'
import person from './Person/Person.js';

class App extends Component {
  state = {
    persons : [
      {name: 'Shubham', age : 28},
      {name : 'Sakib', age : 29}
    ]
  }

  switchNameHandler = (newName) => {
    //console.log('was clicked!');
    //this.state.persons[0].name = 'shubham';
    this.setState({persons: [
      {name: newName, age : 28},
      {name : 'Sakib', age : 29}
    ]});
  }

  render() {
    return (
      <div>
        <h1>Meet the Person component</h1>
        <button onClick={this.switchNameHandler.bind(this, 'Ankit')}>Switch name</button>
        <Person 
          name={this.state.persons[0].name}
          click={this.switchNameHandler}
        />
        <Person name = "Sakib">Shubham's Friend</Person>
      </div>
    );
  }
}

export default App;
